function drawMenu(_ul,arr){$.each(arr,function(index,item){var _li1='<li id="li_'+item.id+'"><a href="javascript:reqUrl('+item.url+','+item.id+')"><i class="menu-icon '+item.className+'"></i>'+item.title+'</a><b class="arrow"></b></li>';var _li2='<li>'+'<a href="javascript:void(0);" class="dropdown-toggle"><i class="menu-icon '+item.className+'"></i>'+item.title+'<b class="arrow fa fa-angle-down"></b></a><b class="arrow"></b></li>';if(item.submenu.length>0){var $ul=$('<ul class="submenu"></ul>');$(_li2).append($ul).appendTo(_ul);drawMenu($($ul),item.submenu);}else{$(_li1).appendTo(_ul)}})}function reqUrl(url,id){$("li").removeClass("active");console.log(url);$("#li_"+id).addClass("active")}